import { Prop, Schema } from '@nestjs/mongoose';
import { IsDateString, IsEnum, IsOptional, IsString } from 'class-validator';
import { Schema as MongooseSchema } from 'mongoose';

import { OPERATORS, VALUE_TYPES } from './constants';
import { TransformObjectId } from './transform-object-id.decorator';

@Schema({
  minimize: true,
  timestamps: {
    updatedAt: 'modified',
  },
})
export class FieldMatchAccessPermissionDTO {
  /**
   * Autogenerated string
   */
  @IsOptional()
  @IsString()
  public _id?: string;
  /**
   * Autogenerated creation date
   */
  @IsOptional()
  @IsDateString()
  @Prop({
    index: true,
    type: Date,
  })
  public created?: string;
  @IsString()
  @Prop({ required: true })
  public formFieldPath: string;
  /**
   * Autogenerated last modified date
   */
  @IsOptional()
  @IsDateString()
  @Prop({
    index: true,
    type: Date,
  })
  public modified?: string;
  @IsEnum(OPERATORS)
  @Prop({
    default: OPERATORS.eq,
    enum: OPERATORS,
    type: 'enum',
  })
  public operator?: OPERATORS;
  @IsString({ each: true })
  @Prop({
    ref: 'role',
    type: MongooseSchema.Types.ObjectId,
  })
  @TransformObjectId()
  public roles: string;
  @IsString()
  @Prop({ required: true })
  public value: string;
  @IsEnum(VALUE_TYPES)
  @Prop({
    default: VALUE_TYPES.string,
    enum: VALUE_TYPES,
    required: true,
    type: 'enum',
  })
  public valueType?: VALUE_TYPES;
}
